// 🧠 AI 음성 리포트 데이터 전처리 및 분석
const documents = items[0].json.documents || [];
const today = new Date().toLocaleDateString('ko-KR');
const yesterday = new Date(Date.now() - 24 * 60 * 60 * 1000).toLocaleDateString('ko-KR');

// 데이터 파싱 및 분석
const logs = documents.map(doc => {
  const fields = doc.fields || {};
  return {
    title: fields.title?.stringValue || '제목 없음',
    price: parseInt(fields.price?.integerValue || '0'),
    address: fields.address?.stringValue || '주소 없음',
    distance: parseInt(fields.distance_m?.integerValue || '0'),
    category: fields.category?.stringValue || '기타',
    condition: fields.condition?.stringValue || '중고',
    timestamp: fields.timestamp?.timestampValue || new Date().toISOString()
  };
});

// 날짜별 필터링 (어제 데이터)
const yesterdayLogs = logs.filter(log => {
  const logDate = new Date(log.timestamp).toDateString();
  return logDate === new Date(Date.now() - 24 * 60 * 60 * 1000).toDateString();
});

// 통계 계산
const totalCount = yesterdayLogs.length;
const totalValue = yesterdayLogs.reduce((sum, log) => sum + log.price, 0);
const avgPrice = totalCount > 0 ? Math.round(totalValue / totalCount) : 0;
const avgDistance = totalCount > 0 ? Math.round(yesterdayLogs.reduce((sum, log) => sum + log.distance, 0) / totalCount) : 0;

// 지역별 집계
const regionStats = yesterdayLogs.reduce((acc, log) => {
  const region = log.address.split(' ').slice(0, 2).join(' '); // 시/구 단위
  if (!acc[region]) {
    acc[region] = { count: 0, totalValue: 0, items: [] };
  }
  acc[region].count += 1;
  acc[region].totalValue += log.price;
  acc[region].items.push(log.title);
  return acc;
}, {});

// 카테고리별 집계
const categoryStats = yesterdayLogs.reduce((acc, log) => {
  if (!acc[log.category]) {
    acc[log.category] = { count: 0, totalValue: 0 };
  }
  acc[log.category].count += 1;
  acc[log.category].totalValue += log.price;
  return acc;
}, {});

// TOP 지역 및 상품
const topArea = Object.entries(regionStats)
  .sort(([,a], [,b]) => b.count - a.count)[0]?.[0] || '정보 없음';
const topItem = Object.entries(categoryStats)
  .sort(([,a], [,b]) => b.totalValue - a.totalValue)[0]?.[0] || '정보 없음';

// 🎧 TTS용 간결한 요약 텍스트 생성
const ttsSummary = `
안녕하세요. 야고바이브 AI 리포트입니다.

${yesterday} 거래 현황을 보고드리겠습니다.
총 ${totalCount}건의 거래가 발생했습니다.
총 거래액은 ${totalValue.toLocaleString()}원이며,
평균 거래액은 ${avgPrice.toLocaleString()}원입니다.

가장 활발한 지역은 ${topArea}이며,
인기 상품은 ${topItem}입니다.

상세한 분석은 첨부된 PDF를 참고해주세요.
감사합니다.
`;

// GPT 프롬프트 생성 (PDF용 상세 분석)
const prompt = `
YAGO VIBE 거래 알림 리포트를 작성해주세요.

📅 **리포트 날짜**: ${yesterday}
📊 **총 거래 건수**: ${totalCount}건
💰 **총 거래액**: ${totalValue.toLocaleString()}원
📏 **평균 거리**: ${avgDistance}m

🏙️ **지역별 거래 현황**:
${Object.entries(regionStats).map(([region, data]) => `  • ${region}: ${data.count}건 (${data.totalValue.toLocaleString()}원)`).join('\n')}

🏷️ **카테고리별 거래 현황**:
${Object.entries(categoryStats).map(([category, data]) => `  • ${category}: ${data.count}건 (${data.totalValue.toLocaleString()}원)`).join('\n')}

**요청사항**:
1. 위 데이터를 바탕으로 전문적이고 간결한 거래 리포트를 작성해주세요.
2. 주요 인사이트와 트렌드를 분석해주세요.
3. 비즈니스 관점에서의 결론을 제시해주세요.
4. 마크다운 형식으로 작성해주세요.
5. 이모지를 적절히 사용해주세요.
`;

return [{
  json: {
    prompt,
    ttsSummary,
    stats: {
      totalCount,
      totalValue,
      avgPrice,
      avgDistance,
      regionStats,
      categoryStats,
      yesterday,
      topArea,
      topItem
    },
    rawData: yesterdayLogs
  }
}];

