// Test all critical endpoints
const tests = [
  {
    name: 'MCP Server Health',
    url: `${$env.MCP_HEALTH_URL}/tools`,
    expectedStatus: 200
  },
  {
    name: 'Session Event Endpoint',
    url: $env.FN_SESSION_EVENT,
    expectedStatus: 200
  },
  {
    name: 'n8n API Health',
    url: `${$env.N8N_BASE}/api/v1/workflows`,
    expectedStatus: 200,
    headers: {
      'Authorization': `Bearer ${$env.N8N_API_KEY}`
    }
  }
];

return tests.map(test => ({
  json: test
}));

// Analyze test results
const results = $input.all();
const total = results.length;
const passed = results.filter(r => r.json.statusCode >= 200 && r.json.statusCode < 300).length;
const failed = total - passed;

const details = results.map(r => {
  const status = r.json.statusCode >= 200 && r.json.statusCode < 300 ? 'âœ…' : 'âŒ';
  return `${status} ${r.json.name}: ${r.json.statusCode}`;
}).join('\n');

const summary = `ðŸ§ª Smoke Test Results\n\nTotal: ${total} | Passed: ${passed} | Failed: ${failed}\n\n${details}`;

return [{
  json: {
    summary: summary,
    total: total,
    passed: passed,
    failed: failed,
    details: details
  }
}];
