const { jsPDF } = require('jspdf');
const doc = new jsPDF();

// ?쒕ぉ
doc.setFontSize(20);
doc.text('YAGO VIBE AI Report', 20, 20);

doc.setFontSize(12);
doc.text('Report Period: Last 7 days', 20, 30);
doc.text(`Generated: ${new Date().toLocaleDateString('ko-KR')}`, 20, 37);

// 媛먯젙 ?듦퀎 怨꾩궛
const summaries = items.map(i => i.json);
const counts = { positive: 0, neutral: 0, negative: 0 };
summaries.forEach(s => {
  const sentiment = (s.sentiment || s.emotion || 'neutral').toLowerCase();
  if (sentiment.includes('positive') || sentiment === '湲띿젙') counts.positive++;
  else if (sentiment.includes('negative') || sentiment === '遺??) counts.negative++;
  else counts.neutral++;
});

const total = summaries.length || 1;

doc.setFontSize(14);
doc.text('========================================', 20, 47);
doc.text('Sentiment Statistics', 20, 55);
doc.text('========================================', 20, 62);

doc.setFontSize(12);
doc.text(`Positive: ${counts.positive} (${(counts.positive / total * 100).toFixed(1)}%)`, 20, 72);
doc.text(`Neutral: ${counts.neutral} (${(counts.neutral / total * 100).toFixed(1)}%)`, 20, 80);
doc.text(`Negative: ${counts.negative} (${(counts.negative / total * 100).toFixed(1)}%)`, 20, 88);
doc.text(`Total Messages: ${total}`, 20, 96);

// 二쇱슂 ?붿빟
doc.setFontSize(14);
doc.text('========================================', 20, 110);
doc.text('Top Summaries', 20, 118);
doc.text('========================================', 20, 125);

let y = 135;
summaries.slice(0, 15).forEach((s, i) => {
  if (y > 270) {
    doc.addPage();
    y = 20;
  }

  const sentiment = (s.sentiment || s.emotion || 'neutral').toLowerCase();
  const label = sentiment.includes('positive') ? '[+]' : sentiment.includes('negative') ? '[-]' : '[=]';

  const text = `${i + 1}. ${label} ${s.summary || 'No summary'}`;
  const lines = doc.splitTextToSize(text, 170);

  doc.setFontSize(10);
  doc.text(lines, 20, y);
  y += lines.length * 6 + 3;
});

// ?명꽣
doc.setFontSize(8);
doc.text('Generated by YAGO VIBE AI System', 20, 285);

const pdfBuffer = doc.output('arraybuffer');
const base64PDF = Buffer.from(pdfBuffer).toString('base64');

return [{
  json: {
    filename: `YAGO_Weekly_Report_${new Date().toISOString().slice(0, 10)}.pdf`,
    positiveCount: counts.positive,
    neutralCount: counts.neutral,
    negativeCount: counts.negative,
    totalCount: total,
    positivePercent: (counts.positive / total * 100).toFixed(1),
    summaryPreview: summaries.slice(0, 3).map(s => s.summary).join(' | ')
  },
  binary: {
    pdf: {
      data: base64PDF,
      mimeType: 'application/pdf',
      fileName: `YAGO_Weekly_Report_${new Date().toISOString().slice(0, 10)}.pdf`
    }
  }
}];
